package sec02;

public class Exam07 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float var1 = 10f;
		float var2 = var1 / 100;    // 10 / 100 -> 0.1  var2 = 0.1f 
		
		if(var2 == 0.1f) {								// double의 0.1
			System.out.println("10%입니다.");
		} else {
			System.out.println("10%가 아닙니다.");
		}
	}
	
}
